import React, {useState,useContext} from 'react'
import {BookContext} from './BookContext'
import Axios from 'axios'
import {API} from '../Helpers/API'

import {Table,Button} from 'react-bootstrap'

function Books() {
    const [newName,setNewName] =useState("");
    const [newPrice, setNewPrice] = useState();
    const [books, setBooks] = useContext(BookContext);

    const updateName = (e) => {
        setNewName(e.target.value);
    }

    const updatePrice = (e) => {
        setNewPrice(e.target.value);
    }

    const handleUpdate = (id) => {
        Axios.put(`${API.api}/update`,{
            name:newName,
            id,
            price:newPrice
        }).then(res=>{
            console.log(res)
        },err=>{console.log(err)})

    }
    return (
        <div className="books">
            <Table striped bordered hover className="book-table">
                <thead>
                    <tr>
                    <th>ID</th>
                    <th>Book Name</th>
                    <th>Price(CAD)</th>
                    <th>Update</th>
                    </tr>
                </thead>
                <tbody>
                    {books.map( (book, key) => (
                        <tr key={key}>
                            <td>{book.id}</td>
                            <td>{book.name} </td>
                            <td><input placeholder="Enter to update name..." 
                            value={newName} onChange={updateName}/></td>
                            <td>{book.price} <input placeholder="Enter to update price..." 
                            value={newPrice} onChange={updatePrice}/></td>
                            <td><Button onClick={()=>handleUpdate(book._id)}>Update</Button></td>
                        </tr>
                    ))}
                </tbody>

            </Table>

        </div>
    )
}

export default Books
