import React, {useContext,useState} from 'react';
import {BookContext} from './BookContext';
import axios from 'axios'
import {Form, Button} from 'react-bootstrap'

const AddBook = () =>{
    const [name, setName] = useState("");
    const [price, setPrice] = useState(0);
    const [books, setBooks] = useContext(BookContext);
    const[id, setId] = useState(1002);

    const updataName = (e) =>{
        setName(e.target.value);
    }

    const updatePrice = (e) =>{
        setPrice(e.target.value);

    }

     const addBook = (e) => {
        setId(id => id+1);
        e.preventDefault();
        setBooks((prevBooks)=> [...prevBooks,
        {
            name,
            price,
            id
        }])
        axios.post("http://localhost:5000/data",{
            name,
            id,
            price
        }).then(res=>{
            console.log(res)
        },err=>{console.log(err)})
    }
    return(
        <Form onSubmite={addBook}>
            <Form.Group controlId="formBasicName">
                <Form.Label>Book Name</Form.Label>
                <Form.Control onChange={updateName} 
                type="text" placeholder="Enter book name" value={name} />
            </Form.Group>

            <Form.Group controlId="formBasicPrice">
                <Form.Label>Price</Form.Label>
                <Form.Control onChange={updatePrice}
                type="text" placeholder="Enter book price" value={price} />
            </Form.Group>

            <Button variant="primary" type="submit">
                Add Book
            </Button>
        </Form>

        // <form onSubmit={addBook} >
        //     <input onChange={updataName} type="text" name="name" value={name} />
        //     <input onChange={updatePrice} type="text" name="price" value={price} />
        //     <button>Submit</button>

        // </form>
    )
}

export default AddBook;
