import React, {useContext,useState} from 'react';
import {BookContext} from './BookContext';

const AddBook = () =>{
    const [name, setName] = useState("");
    const [price, setPrice] = useState(0);
    const [books, setBooks] = useContext(BookContext);
    const[id, setId] = useState(1003);

    const updataName = (e) =>{
        setName(e.target.value);
    }

    const updatePrice = (e) =>{
        setPrice(e.target.value);

    }

     const addBook = (e) => {
        setId(id+1);
        e.preventDefault();
        //prevMoives to hold the previous movies, then add another new object
        setBooks(prevBooks => [...prevBooks, 
            {name:name,
            price:price,
            id:id
            }]);
    }
    return(
        <form onSubmit={addBook} >
            <input onChange={updataName} type="text" name="name" value={name} />
            <input onChange={updatePrice} type="text" name="price" value={price} />
            <button>Submit</button>

        </form>
    )
}

export default AddBook;
